INSERT INTO `sliders` (`slider_id`, `title`, `image`, `description`, `recipe_id`) VALUES
(4, 'Súp sữa rau củ', 'cooky-recipe-637113447153274111.jpg', 'Vừa dễ nấu, vừa dễ ăn, vừa đầy đủ dinh dưỡng. Bữa sáng nhanh gọn với món này là có thể có thêm nhiều thời gian rãnh rỗi hơn dành cho bản thân mình!', 45),
(5, 'Tôm xào miến Hong Kong', 'cooky-recipe-cover-r51153.jpg', 'Tôm xào miến Hồng Kông là món ăn được nhiều người yêu thích bởi vị tươi giòn của rau củ, từng sợi miến mềm dai hoà quyện cùng với vị ngọt của tôm. Đây sẽ là một món ngon hấp dẫn mà lại dễ làm, ngay cả khi bạn có rất ít thời gian chuẩn bị. Các chị em hãy tham khảo cách làm miến xào tôm thịt sau của Vedan để thay đổi những bữa cơm cho gia đình mình.', 46),
(6, 'Mì Ý tôm sốt kem óc chó', 'recipe50862-cook-step6-637115966781475224.jpg', 'Nếu bạn đang loay hoay trong việc lên thực đơn cho cả gia đình trong dịp cuối tuần thì mì Ý tôm sốt kem óc chó là sự lựa chọn không thể tuyệt vời hơn. Cuối tuần này bạn chỉ cần bỏ chút thời gian vào bếp để mang đến cho gia đình mình một món ăn bổ dưỡng và tuyệt vời rồi đó. Hy vọng thông qua hướng dẫn của Ecomil, bạn sẽ biết thêm bí quyết nhanh gọn để làm mì Ý tôm sốt kem óc chó này nhé!', 47),
(7, 'Mì Ý sốt thị bò bằm', 'sliderA_04.jpg', 'Các mẹ muốn tự tay chuẩn bị món mỳ ý sốt cà chua bò bằm đúng chuẩn nhà hàng cho bé và ông xã thưởng thức vào sáng cuối tuần nhưng chưa biết cách? Hãy học cách làm mỳ ý sốt cà chua bò bằm thơm ngon đúng chuẩn ngay hôm nay nhé!', 49),
(8, 'Hủ tiếu xương và bò viên', 'cach-nau-hu-tieu-bo-vien-ngon-5.jpg', 'Hủ tiếu bò viên là món ngon đặc sắc của người Hoa di cư vào VN. Ngày nay, đi đâu cũng thấy quán bán hủ tiêu từ rẻ đến đắt nhưng mình thích nhất vẫn là hủ tiếu bò viên. Tô hủ tiếu bò viên ngon phải có nước dùng thanh ngọt, trong vắt; bò viên từ thịt bò tươi, sợi hủ tiếu dai dai, mềm mềm... Nói đến thì phải bắt tay vào bếp thôi nào...', 48),
(9, 'Salad rau trộn sốt mayonnnais', 'sliderA_04.jpg', 'Salad là một trong những món ăn kèm trong những bửa cơm hay cả những khi thảnh thơi đều có thể dùng được, nó không chỉ giúp các chị em có làn da đẹp mà còn giúp cải thiện cân nặng nữa. Cách làm salad từ các nguyên liệu sẵn có rất đơn giản.', 50);
